import Social_inline from "./Social_inline";
import logo from "../assets/Got7_2022_logo.png";
import React, { useState } from "react";
import MenuIcon from "./MenuIcon";

const albumsData = [
  {
    id: 1,
    imageSrc:
      "https://blogger.googleusercontent.com/img/a/AVvXsEh9oMSuhEV74m2MwTvFV7z_EJ8fjZQLgAIlSdEjX4meg8U6aVCv7SvDihAjja7PEOFDK4DEG23GtH1V437Q2zISv5X9oYt8PrUdw1B5uAIsuczI4ZgM8j24vPNAW-lPg4G96v9SoN4UkHL0YdBl1joNRVt1EqDuXCEsShTPirVJYOioqUeihUXXhZOG",
    albumName: "GOT7 IS OUR NAME",
  },
  {
    id: 2,
    imageSrc: "https://inwfile.com/s-l/3azqfp.jpg",
    albumName: "TURBURLENCE",
  },
  {
    id: 3,
    imageSrc:
      "https://4.bp.blogspot.com/-KZm3AJVUvKM/Ut3hlgp7V9I/AAAAAAAABBY/ewfAHWZRkfA/s1600/got7.jpg",
    albumName: "GOT IT?",
  },
];

function Discography() {
  const [isHovered, setIsHovered] = useState(false);

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleHoverExit = () => {
    setIsHovered(false);
  };

  return (
    <>
      <style>
        {`
          html, body {
            background-color: #F3F6F4;
          }

        .hovered-vdo {
          background-image: linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)), url("https://64.media.tumblr.com/9eaa9c7f490bb019d1bc31ed6e564f7b/7c267b20b21ac5fb-63/s540x810/9b3111e4d6510b659275737f0f53dcdcf9600231.gif");
          background-size: cover;
          background-repeat: no-repeat;
          object-fit: cover;
        }

        .vdo-cover{
            width: 187px;
            height: 105px;
        }

        @media (max-width: 600px) {
            .head-logo{
                display: flex;
            }

            .app{
                display: flex;
                flex-direction: column;
                margin-top: 50px
            }

            .logo-con{
              margin-top: 130px;
            }

            .song {
              display: flex;
            }

            .vdo-cover{
              width: 190px;
              height: 140px;
          }

          .song-info{
            font-size: 10px;
          }

          .song-info h1{
            padding: 0px;
            font-size: 14px;
          }

          .title {
            margin-top: -10px;
          }

          .views{
            margin: 0px
          }

          .vdo-player iframe{
            width: 200px;
            height: 108px;
          }

        `}
      </style>

      <div
        className="head-logo"
        style={{
          display: "flex",
          height: "60px",
          alignItems: "center",
          justifyContent: "space-between",
          position: "relative",
        }}
      >
        <Social_inline />
        <div className="logo-con">
          <img
            src={logo}
            style={{ height: "60px", marginLeft: "-110px", marginTop: "50px" }}
          ></img>
        </div>
        <div
          className="menu-icon-container"
          style={{ margin: "30px 20px 0px 0px" }}
        >
          <MenuIcon />
        </div>
      </div>
      <div className="app">
        <div className="disc-heading">
          <h1
            style={{
              textAlign: "center",
              marginTop: "60px",
              color: "#3C8350",
              fontSize: "38px",
            }}
          >
            DISCOGRAPHY
          </h1>
        </div>

        <div className="disc-body"
          style={{
            backgroundColor: "white",
            width: "80%",
            height: "auto",
            marginTop: "50px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            margin: "0 auto",
            flexWrap: "wrap",
            padding: "20px",
          }}
        >
          <div
            className="disc-show-pop"
            style={{
              width: "100%",
              display: "flex",
              flexWrap: "wrap",
              justifyContent: "space-around",
            }}
          >
            {albumsData.map((album) => (
              <div className="album-container" key={album.id}>
                <div className="album" style={album_part}>
                  <div className="album-cover">
                    <img
                      src={album.imageSrc}
                      alt={album.albumName}
                      style={album_cover}
                    />
                  </div>
                  <div className="name-album">
                    <h4>{album.albumName}</h4>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="song-suggestion">
          <div
            className="song"
            onMouseEnter={handleHover}
            onMouseLeave={handleHoverExit}
          >
            {isHovered ? (
              <div
                className="hovered-vdo"
                style={{
                  display: "flex",
                  width: "80%",
                  margin: "0 auto",
                  paddingRight: "40px",
                  paddingTop: "50px",
                  paddingBottom: "50px",
                }}
              >
                <div className="vdo-player">
                  <iframe
                    width="460px"
                    height="215px"
                    src="https://www.youtube.com/embed/IZ0oQ6nzKxo?controls=0"
