import * as React from 'react';
import Box from '@mui/material/Box';
import Avatar from '@mui/material/Avatar';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';

function MenuIcon() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };


  return (
  <>
        <style>
        {`
        .hovered-vdo {
          background-image: linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)), url("https://64.media.tumblr.com/9eaa9c7f490bb019d1bc31ed6e564f7b/7c267b20b21ac5fb-63/s540x810/9b3111e4d6510b659275737f0f53dcdcf9600231.gif");
          background-size: cover;
          background-repeat: no-repeat;
          object-fit: cover;
        `}
      </style>
    <React.Fragment>
      <Box sx={{ display: 'flex', alignItems: 'center', textAlign: 'center',}}>
        <Tooltip title="Menu">
          <IconButton
            onClick={handleClick}
            size="small"
            sx={{ ml: 2,}}
            aria-controls={open ? 'menu' : undefined}
            aria-haspopup="true"
            aria-expanded={open ? 'true' : undefined}
          >
            <Avatar>
              <img src="https://cdn-icons-png.flaticon.com/512/55/55003.png" style={{width:"16px"}}></img>
            </Avatar>
          </IconButton>
        </Tooltip>
      </Box>
      <Menu
        anchorEl={anchorEl}
        id="menu"
        open={open}
        onClose={handleClose}
        onClick={handleClose}
      >
        <MenuItem onClick={handleClose}>
        <a href="/" className="navbar-link">HOME</a>
        </MenuItem>
        <MenuItem onClick={handleClose}>
        <a href="/discography" className="navigate">DISCOGRAPHY</a>
        </MenuItem>
        <MenuItem onClick={handleClose}>
        <a href="/notice" className="navbar-link">NOTICE</a>
        </MenuItem>
        <MenuItem onClick={handleClose}>
        <a href="/schedule" className="navbar-link">SCHEDULE</a>
        </MenuItem>
        <MenuItem onClick={handleClose}>
        <a href="/contact" className="navbar-link">CONTACT</a>
        </MenuItem>
        <MenuItem onClick={handleClose}>
        <a href="/about" className="navbar-link">ABOUT</a>
        </MenuItem>
      </Menu>
    </React.Fragment>
    </>
  );
}
export default MenuIcon;
