import Social_inline from "./Social_inline";
import logo from "../assets/Got7_2022_logo.png";
import React, { useState } from "react";
import MenuIcon from "./MenuIcon";

function Discography(params) {
  const [isHovered, setIsHovered] = useState(false);

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleHoverExit = () => {
    setIsHovered(false);
  };

  function Card(user) {
    const { picture, job, hobby }= user;
    return (
        <div className="card_container">
            <p className="card_b">Name: {name}</p>
            <p className="card_b">Job: {job}</p>
            <p className="card_b">Hobby: {hobby}</p>
        </div>
    )
}

  return (
    <>
      <style>
        {`
          html, body {
            background-color: #F3F6F4;
          }

        .hovered-vdo {
          background-image: linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)), url("https://64.media.tumblr.com/9eaa9c7f490bb019d1bc31ed6e564f7b/7c267b20b21ac5fb-63/s540x810/9b3111e4d6510b659275737f0f53dcdcf9600231.gif");
          background-size: cover;
          background-repeat: no-repeat;
          object-fit: cover;
        }

        .vdo-cover{
            width: 187px;
            height: 105px;
        }

        @media (max-width: 600px) {
            .head-logo{
                display: flex;
            }

            .app{
                display: flex;
                flex-direction: column;
                margin-top: 50px
            }

            .logo-con{
              margin-top: 130px;
            }

            .song {
              display: flex;
            }

            .vdo-cover{
              width: 190px;
              height: 140px;
          }

          .song-info{
            font-size: 10px;
          }

          .song-info h1{
            padding: 0px;
            font-size: 14px;
          }

          .title {
            margin-top: -10px;
          }

          .views{
            margin: 0px
          }

          .vdo-player iframe{
            width: 200px;
            height: 108px;
          }

        `}
